import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MovieList = (props) => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [userCountry, setUserCountry] = useState(null);
  const [errorMsg, setErrorMsg] = useState(null);
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    getUserCountry();
  }, []);

  useEffect(() => {
    if (userCountry) {
      fetchMoviesByCountry(userCountry);
    }
  }, [userCountry]);

  const handleClick = async (movie) => {
    try {
      const detailsResponse = await fetchMovieDetails(movie.Title);
      const trailerId = await fetchMovieTrailer(movie.Title);
      setSelectedMovie({ ...movie, description: detailsResponse.Plot, trailer: trailerId });
    } catch (error) {
      console.error('Error fetching movie details and trailer:', error);
    }
  };

  const fetchMoviesByCountry = async (country) => {
    try {
      const response = await axios.get('https://api.themoviedb.org/3/discover/movie', {
        params: {
          api_key: '00e58447b107bbf5456c351874f74894',
          region: country,
        },
      });
      setMovies(response.data.results);
    } catch (error) {
      console.error('Error fetching movies by country:', error);
    }
  };

  const fetchMovieDetails = async (movieTitle) => {
    try {
      const response = await axios.get(`http://www.omdbapi.com/?t=${movieTitle}&apikey=70c47435`);
      return response.data;
    } catch (error) {
      console.error('Error fetching movie details:', error);
      return null;
    }
  };

  const fetchMovieTrailer = async (movieTitle) => {
    try {
      const response = await axios.get('https://www.googleapis.com/youtube/v3/search', {
        params: {
          q: `${movieTitle} trailer`,
          part: 'snippet',
          maxResults: 1,
          type: 'video',
          key: 'AIzaSyB0JWfa-UI4fXgayxvo4KFgDj6BU7WUlbw'
        }
      });
      return response.data.items[0].id.videoId;
    } catch (error) {
      console.error('Error fetching movie trailer:', error);
      return null;
    }
  };

  const addToFavorites = (movie) => {
    const isAlreadyAdded = props.movies.some((fav) => fav.imdbID === movie.imdbID);

    if (!isAlreadyAdded) {
      props.AddFavourites(movie);
    }
  };

  const FavouriteComponent = props.favouriteComponent;

  return (
    <>
      {errorMsg && <p>{errorMsg}</p>}
      {movies.map((movie, index) => (
        <div className='image-container d-flex justify-content-start m-3' key={index}>
          <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt='movie' onClick={() => handleClick(movie)} />
          <div onClick={() => addToFavorites(movie)} className='overlay d-flex align-items-center justify-content-center'>
            <FavouriteComponent />
          </div>
        </div>
      ))}
      {selectedMovie && (
        <div className='image-container d-flex justify-content-start m-3'>
          <div className='selected-movie-details'>
            <div className='movie-info'>
              <img src={selectedMovie.Poster} alt='selected movie poster' />
              <div>
                <h2>{selectedMovie.Title}</h2>
                <p>{selectedMovie.description}</p>
              </div>
            </div>
            <div className='trailer-container'>
              <iframe
                width='560'
                height='315'
                src={`https://www.youtube.com/embed/${selectedMovie.trailer}`}
                title='YouTube video player'
                frameBorder='0'
                allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
                allowFullScreen
              ></iframe>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default MovieList;
